import React from "react";
import clsx from "clsx";

const copy_button = (props) => {
  const { position, npm, yarn } = props;

  // const copy_function = () => {
  //   const npm_text = npm.current.textContent;
  //   const textArea = document.createElement("textarea");
  //   textArea.value = npm_text;
  //   document.body.appendChild(textArea);
  //   textArea.select();
  //   document.execCommand("copy");
  //   document.body.removeChild(textArea);
  // };
    const copy_function = (npm) => {
      navigator.clipboard.writeText(npm)
        .then(() => {
          console.log('Text copied to clipboard');
        })
        .catch((error) => {
          console.error('Copy failed:', error);
        });}

  return (
    <div
      onClick={() => {
        copy_function();
      }}
      className={clsx("copy_button", "react_intro_position")}
    >
      copy
    </div>
  );
};

export default copy_button;
