import React from "react";
import clsx from "clsx";

const copy_button = (props) => {
  const { position, npm, who_use_copy } = props;

  const copy_function = () => {
    navigator.clipboard
      .writeText(npm.current.textContent)
      .then(() => {
        console.log("複製到剪貼簿");
      })
      .catch((error) => {
        console.error("複製失敗原因", error);
      });
  };

  return (
    <div
      onClick={() => {
        copy_function();
      }}
      className={clsx("copy_button", position)}
    >
      copy
    </div>
  );
};

export default copy_button;
